const additiveCategories = [
    {
        id: "preservatives",
        name: "保存料",
        riskLevel: "高リスク",
        colorClass: "bg-red-500",
        description: "魂の自然な循環を妨げ、エネルギーの停滞を引き起こす。古来より「時を止める物質」として忌避されてきた。",
        examples: ["ソルビン酸カリウム", "安息香酸ナトリウム", "亜硝酸ナトリウム", "BHA", "BHT"],
        alternatives: ["塩麹", "酢", "レモン汁", "ローズマリーエキス"]
    },
    // 残りの添加物データ...
    {
        id: "natural-flavors",
        name: "天然香料",
        riskLevel: "低リスク",
        colorClass: "bg-green-500",
        description: "自然界の精気を含むが、本来の植物や食材から分離されているため、完全な生命エネルギーではない。",
        examples: ["バニラエキス", "アーモンドエッセンス", "レモンオイル"],
        alternatives: ["実際の香草・スパイス", "果皮", "花びら"]
    }
];

export default additiveCategories;
